mapper.select:
select 

	,(select order_content from base_order where base_order.id=pay_record.out_trade_no) as order_content
from pay_record

where user_id = #{userId,jdbcType=VARCHAR}

 order by ${sort} ${order},pay_time desc, id desc limit #{offset}, #{limit}

select count(1)
from pay_record

where user_id = #{userId,jdbcType=VARCHAR}
